import React, { useEffect } from "react";
import { makeStyles } from "@material-ui/core/styles";
import Grid from "@material-ui/core/Grid";
import main from "../media/main.svg";
import { Link } from "react-router-dom";
import Button from "@material-ui/core/Button";
import {
  Image,
  Box,
  chakra,
  Container,
 
  Stack,
  Text,
  VisuallyHidden,
  Input,
  IconButton,
  useColorModeValue,
} from '@chakra-ui/react';
import { ReactNode } from 'react';
import { FaInstagram, FaTwitter, FaYoutube } from 'react-icons/fa';
import { BiMailSend } from 'react-icons/bi';

const useStyles = makeStyles((theme) => ({
  root: {
    flexGrow: 1,
    marginTop: 50,
  },
  paper: {
    padding: theme.spacing(2),
    textAlign: "center",
    color: theme.palette.text.secondary,
  },
  mainImg: {
    maxWidth: "90%",
  },
  
}));

export default function Landing() {
  const classes = useStyles();
  const SocialButton = ({
    children,
    label,
    href,
  }) => {
    return (
      <chakra.button
        bg={useColorModeValue('blackAlpha.100', 'whiteAlpha.100')}
        rounded={'full'}
        w={8}
        h={8}
        cursor={'pointer'}
        as={'a'}
        href={href}
        display={'inline-flex'}
        alignItems={'center'}
        justifyContent={'center'}
        transition={'background 0.3s ease'}
        _hover={{
          bg: useColorModeValue('blackAlpha.200', 'whiteAlpha.200'),
        }}>
        <VisuallyHidden>{label}</VisuallyHidden>
        {children}
      </chakra.button>
    );
  };

  useEffect(() => {
    localStorage.clear();
  }, []);

  return (
    <div className={classes.root}>
      <Grid>
      <>
      <Box>

        <Image 
        src="https://scontent.ftun16-1.fna.fbcdn.net/v/t1.15752-9/278866128_353580116605305_5731967001903309706_n.png?_nc_cat=109&ccb=1-5&_nc_sid=ae9488&_nc_ohc=lwy7HKyo9xAAX_svTgW&_nc_ht=scontent.ftun16-1.fna&oh=03_AVJJJJg2pZ3ZpGF9vRGU5dPVn9avjJMzrfqnFl8h1bYsog&oe=628B3336" 
        ml={450} mb={1} mt={2}
        >

        </Image>

        <Box

          color={useColorModeValue('gray.700', 'gray.200')}>
          <Container ml={1100} mt={-55} mb={50}>
            <Stack spacing={6}>
              <Stack direction={'row'} spacing={6}>
                <SocialButton label={'Twitter'} href={'#'}>
                  <FaTwitter />
                </SocialButton>
                <SocialButton label={'YouTube'} href={'#'}>
                  <FaYoutube />
                </SocialButton>
                <SocialButton label={'Instagram'} href={'#'}>
                  <FaInstagram />
                </SocialButton>
              </Stack>
            </Stack>

          </Container>
        </Box>
      </Box>
    </>
      </Grid>
      <Grid item container alignItems="center" justify="center">
        <Grid item xs={10} sm={6}>
          <div  > 
            <h1>Bienvenue sur notre plateforme</h1>
            <h2>Commencer votre cours en ligne maintenant</h2>
            <h2>en créent un compte en cliquant sur ce button</h2>
            <Button type="primary" variant="contained" color="primary">
              <Link
                style={{ textDecoration: "none", color: "white" }}
                to="/signup"
              >
                Inscription élève
              </Link>
            </Button>
{/* 
            <div  style={{marginTop : 15}}> 
              <h3>vous êtes professeur ?</h3>
              <Button type="primary" color="primary" variant="contained">
                <Link
                  style={{ textDecoration: "none", color: "white" }}
                  to="/signupProf"
                >
                  Inscription professeur
                </Link>
              </Button>
            </div>*/}
          </div>
          <div style={{marginTop : 15 }}>
            <h3>Vous avez un compte ?</h3>
            <Button type="primary" color="primary" variant="contained">
              <Link
                style={{ textDecoration: "none", color: "white" }}
                to="/signin"
              >
                Login
              </Link>
            </Button>
          </div>
        </Grid>
        <Grid item xs={10} sm={6}>
          <img className={classes.mainImg} src={main}/>
        </Grid>

      </Grid>
    </div>
  );
}
