import { Button, Img } from "@chakra-ui/react";
import { Box, Grid } from "@material-ui/core";
import Axios from "axios";
import React, { useEffect, useState } from "react";
import { BrowserRouter as Router, Route, Switch } from "react-router-dom";
import "./App.css";
import Abouus from "./Components/Abouus";
import AddCourse from "./Components/addCourse";
import AvantNav from "./Components/AvantNav";
import Avisvisiteur from "./Components/Avisvisiteur";
import Comments from "./Components/Comments";
import Contact from "./Components/Contact";
import CoursePage from "./Components/coursePage";
import Courses from "./Components/courses";
import CoursesByChapter from "./Components/coursesByChapter";
import GestionCours from "./Components/gestionCours";
import GestionEleves from "./Components/gestionEleves";
import GestionProfs from "./Components/gestionProfs";
import Landing from "./Components/Landing";
import LargeWithNewsletter from "./Components/LargeWithNewsletter";
import NavBar from "./Components/Navbar";
import SignIn from "./Components/SignIn";
import SignUp from "./Components/SignUp";
import SignupProf from "./Components/signupProf";

import UpdateCourse from "./Components/updateCourse";
import UserContext from "./context/userContext";

const App = () => {
  const [userData, setUserData] = useState({});

  useEffect(
    () => {
      const checkLoggedIn = async () => {
        let token = localStorage.getItem("token");

        if (token === null) {
          localStorage.clear();
          // history.push("/signin");
        }
        /*const tokenRes = await Axios.post(
          "https://alpha-school.herokuapp.com/api/checkToken",
          null,
          {
            headers: { token: token },
          }
        );
        console.log(tokenRes);
        if (tokenRes.data) {*/
        const userRes = await Axios.get("http://localhost:3001/api/userById", {
          headers: { token: token },
        });
        setUserData({
          token,
          role: userRes.data.role,
          user: userRes.data.user,
          isAuth: userRes.data.auth,
        });
      };
      checkLoggedIn();
    },
    [],
    [userData]
  );

  return (
    <>
    <div className="App">
      <UserContext.Provider value={{ userData, setUserData }}>
        <Router>
          <Grid container direction="column">
            <Grid item>
         
    <AvantNav/>
   
              <NavBar/>
             
        <Button position={"fixed"} marginLeft={"600px"} background="whiteAlpha.900">
          <Img src="data:image/png;base64," w={"50px"} h={"50px"} >
         
          </Img>
          </Button>
            </Grid>
        
            <Grid item container>
  
              <Grid item xs={false} sm={2} />
           
      
         
         
         
              <Grid item xs={12} sm={12} >
            
                {userData.role === "0" ? (
                  <Switch>
                    <Route exact path="/" component={Landing}  />
                    <Route exact path="/signin" component={SignIn} />
                    <Route exact path="/signup" component={SignUp} />
                    <Route exact path="/signupProf" component={SignupProf} />
                    <Route exact path="/courses" component={Courses} />

                    <Route
                      exact
                      path="/gestionCours"
                      component={GestionCours}
                    />
                    <Route
                      exact
                      path="/gestioneleves"
                      component={GestionEleves}
                    />
                    <Route
                      exact
                      path="/gestionProfs"
                      component={GestionProfs}
                    />
                    <Route exact path="/addCourse" component={AddCourse} />
                    <Route
                      exact
                      path="/updateCourse"
                      component={UpdateCourse}
                    />
                  </Switch>
                ) : (
                  <Switch>
                    <Route exact path="/" component={Landing} />
                    <Route exact path="/signin" component={SignIn} />
                    <Route exact path="/signup" component={SignUp} />
                    <Route exact path="/signupProf" component={SignupProf} />
                    <Route exact path="/courses" component={Courses} />
                    <Route
                      exact
                      path="/coursePage/:id"
                      component={CoursePage}
                    />
                    <Route
                      exact
                      path="/chaptires/:id"
                      component={CoursesByChapter}
                    />
                  </Switch>
                )}
              </Grid>
          
              <Grid item xs={false} sm={2}></Grid>
            </Grid>
          </Grid>
        </Router>
      </UserContext.Provider>
    </div>

  
    <LargeWithNewsletter/>
    </>
  );
};

export default App;
