import React, { useContext, useEffect } from "react";
import { makeStyles } from "@material-ui/core/styles";
import AppBar from "@material-ui/core/AppBar";
import Toolbar from "@material-ui/core/Toolbar";
import Typography from "@material-ui/core/Typography";
import Button from "@material-ui/core/Button";
import Grid from "@material-ui/core/Grid";
import { Link } from "react-router-dom";
import { useHistory } from "react-router-dom";
import UserContext from "../context/userContext";
import HomeRoundedIcon from "@material-ui/icons/HomeRounded";
import VpnKeyRoundedIcon from "@material-ui/icons/VpnKeyRounded";
import AddCircleOutlineRounded from "@material-ui/icons/AddCircleOutlineRounded";
import MenuBookRoundedIcon from "@material-ui/icons/MenuBookRounded";
import ExitToAppRoundedIcon from "@material-ui/icons/ExitToAppRounded";
import FaceSharpIcon from "@material-ui/icons/FaceSharp";
import PersonRoundedIcon from "@material-ui/icons/PersonRounded";
import {
  Image,
  Box,
  chakra,
  Container,
 
  Stack,
  Text,
  VisuallyHidden,
  Input,
  IconButton,
  useColorModeValue,
} from '@chakra-ui/react';
import { ReactNode } from 'react';
import { FaInstagram, FaTwitter, FaYoutube } from 'react-icons/fa';
import { BiMailSend } from 'react-icons/bi';
const SocialButton = ({
  // children,
  label,
  href,
}) => {
  return (
    <chakra.button
      bg={useColorModeValue('blackAlpha.100', 'whiteAlpha.100')}
      rounded={'full'}
      w={8}
      h={8}
      cursor={'pointer'}
      as={'a'}
      href={href}
      display={'inline-flex'}
      alignItems={'center'}
      justifyContent={'center'}
      transition={'background 0.3s ease'}
      _hover={{
        bg: useColorModeValue('blackAlpha.200', 'whiteAlpha.200'),
      }}>
      <VisuallyHidden>{label}</VisuallyHidden>
      {/* {children} */}
    </chakra.button>
  );
};
const useStyles = makeStyles((theme) => ({
  root: {
    flexGrow: 1,
  },
  menuButton: {
    marginRight: theme.spacing(2),
  },
  title: {
    flexGrow: 1,
  },
  hide: {
    display: "none",
  },
  button: {
    margin: theme.spacing(2),
   
  },

}));

export default function NavBar() {
  const history = useHistory();
  const classes = useStyles();
  const { userData, setUserData } = useContext(UserContext);
  localStorage.setItem("isAuth", userData.isAuth);
  /*localStorage.setItem("admin", userData.user.admin);
  localStorage.setItem("role", userData.role);*/

  const logout = () => {
    setUserData({
      isAuth: false,
      token: undefined,
      user: undefined,
    });
    localStorage.clear();
    history.push("/signin");
  };

  useEffect(() => {
    const onChargeNavBar = () => {
      if (userData.isAuth === false) {
        return (
          <>
                <>
      <Box>

        <Image 
        src="https://scontent.ftun16-1.fna.fbcdn.net/v/t1.15752-9/278866128_353580116605305_5731967001903309706_n.png?_nc_cat=109&ccb=1-5&_nc_sid=ae9488&_nc_ohc=lwy7HKyo9xAAX_svTgW&_nc_ht=scontent.ftun16-1.fna&oh=03_AVJJJJg2pZ3ZpGF9vRGU5dPVn9avjJMzrfqnFl8h1bYsog&oe=628B3336" 
        ml={450} mb={1} mt={2}
        >

        </Image>

        <Box

          color={useColorModeValue('gray.700', 'gray.200')}>
          <Container ml={1100} mt={-55} mb={50}>
            <Stack spacing={6}>
              <Stack direction={'row'} spacing={6}>
                <SocialButton label={'Twitter'} href={'#'}>
                  <FaTwitter />
                </SocialButton>
                <SocialButton label={'YouTube'} href={'#'}>
                  <FaYoutube />
                </SocialButton>
                <SocialButton label={'Instagram'} href={'#'}>
                  <FaInstagram />
                </SocialButton>
              </Stack>
            </Stack>

          </Container>
        </Box>
      </Box>
    </>
            <Button
              startIcon={<HomeRoundedIcon style={{ fill: "white" }} />}
              className={classes.button}
            >
              <Link style={{ textDecoration: "none", color: "white" }} to="/">
                ACCUEIL
              </Link>
            </Button>
            <Button
              startIcon={<VpnKeyRoundedIcon style={{ fill: "white" }} />}
              className={classes.button}
            >
              <Link
                style={{ textDecoration: "none", color: "white" }}
                to="/signin"
              >
                LOGIN
              </Link>
            </Button>
            <Button
              startIcon={<AddCircleOutlineRounded style={{ fill: "white" }} />}
              className={classes.button}
            >
              <Link
                style={{ textDecoration: "none", color: "white" }}
                to="/signup"
              >
                INSCRIPTION
              </Link>
            </Button>
          </>
        );
      }
    };

    const onChargeNavBarAuth = () => {
      if (
        userData.role === "0" &&
        userData.user.admin === true &&
        userData.isAuth
      ) {
        return (
          <>
            <Button>
              <Link
                style={{ textDecoration: "none", color: "white" }}
                to="/gestioneleves"
              >
                Gestion des éleves
              </Link>
            </Button>
            <Button>
              <Link
                style={{ textDecoration: "none", color: "white" }}
                to="/gestioncours"
              >
                Gestion des cours
              </Link>
            </Button>
            <Button
              startIcon={<PersonRoundedIcon style={{ fill: "white" }} />}
              className={classes.button}
            >
              <Link
                style={{ textDecoration: "none", color: "white" }}
                to="/gestionprofs"
              >
                Gestion des profs
              </Link>
            </Button>
            <Button
              onClick={logout}
              startIcon={<ExitToAppRoundedIcon style={{ fill: "white" }} />}
              className={classes.button}
            >
              <Link
                style={{ textDecoration: "none", color: "white" }}
                to="/signin"
              >
                DECONNEXION
              </Link>
            </Button>
          </>
        );
      }
    };

    const onChargeNavBarStudent = () => {
      if (userData.role === "1") {
        return (
          <>
            <Button
              startIcon={<MenuBookRoundedIcon style={{ fill: "white" }} />}
              className={classes.button}
            >
              <Link
                style={{ textDecoration: "none", color: "white" }}
                to="/courses"
              >
                Mes cours
              </Link>
            </Button>

            <Button
              onClick={logout}
              startIcon={<ExitToAppRoundedIcon style={{ fill: "white" }} />}
              className={classes.button}
            >
              <Link
                style={{ textDecoration: "none", color: "white" }}
                to="/signin"
              >
                DECONNEXION
              </Link>
            </Button>
          </>
        );
      }
    };

    const onChangeNavBarProf = () => {
      if (userData.role === "0") {
        return (
          <>
            <Button
              startIcon={<FaceSharpIcon style={{ fill: "white" }} />}
              className={classes.button}
            >
              <Link
                style={{ textDecoration: "none", color: "white" }}
                to="/gestioneleves"
              >
                Gestion des éleves
              </Link>
            </Button>
            <Button
              startIcon={<MenuBookRoundedIcon style={{ fill: "white" }} />}
              className={classes.button}
            >
              <Link
                style={{ textDecoration: "none", color: "white" }}
                to="/gestioncours"
              >
                Gestion des cours
              </Link>
            </Button>
            <Button
              onClick={logout}
              startIcon={<ExitToAppRoundedIcon style={{ fill: "white" }} />}
              className={classes.button}
            >
              <Link
                style={{ textDecoration: "none", color: "white" }}
                to="/signin"
              >
                DECONNEXION
              </Link>
            </Button>
          </>
        );
      }
    };

    onChargeNavBar();
    onChangeNavBarProf();
    onChargeNavBarAuth();
    onChargeNavBarStudent();
  }, []);

  const onChargeNavBar = () => {
    if (userData.isAuth === false) {
      return (
        <>
          
          <Button
            startIcon={<HomeRoundedIcon style={{ fill: "white" }} />}
            className={classes.button}
          >
            <Link style={{ textDecoration: "none", color: "white" }} to="/">
              ACCUEIL
            </Link>
          </Button>
          <Button
            startIcon={<VpnKeyRoundedIcon style={{ fill: "white" }} />}
            className={classes.button}
          >
            <Link
              style={{ textDecoration: "none", color: "white" }}
              to="/signin"
            >
              LOGIN
            </Link>
          </Button>
          <Button
            startIcon={<AddCircleOutlineRounded style={{ fill: "white" }} />}
            className={classes.button}
          >
            <Link
              style={{ textDecoration: "none", color: "white" }}
              to="/signup"
            >
              INSCRIPTION
            </Link>
          </Button>
        </>
      );
    } else if (
      userData.role === "0" &&
      userData.user.admin === true &&
      userData.isAuth
    ) {
      return (
        <>
          <Button
            startIcon={<FaceSharpIcon style={{ fill: "white" }} />}
            className={classes.button}
          >
            <Link
              style={{ textDecoration: "none", color: "white" }}
              to="/gestioneleves"
            >
              Gestion des éleves
            </Link>
          </Button>
          <Button
            startIcon={<MenuBookRoundedIcon style={{ fill: "white" }} />}
            className={classes.button}
          >
            <Link
              style={{ textDecoration: "none", color: "white" }}
              to="/gestioncours"
            >
              Gestion des cours
            </Link>
          </Button>
          <Button
            startIcon={<PersonRoundedIcon style={{ fill: "white" }} />}
            className={classes.button}
          >
            <Link
              style={{ textDecoration: "none", color: "white" }}
              to="/gestionprofs"
            >
              Gestion des profs
            </Link>
          </Button>
          <Button
            onClick={logout}
            startIcon={<ExitToAppRoundedIcon style={{ fill: "white" }} />}
            className={classes.button}
          >
            <Link
              style={{ textDecoration: "none", color: "white" }}
              to="/signin"
            >
              DECONNEXION
            </Link>
          </Button>
        </>
      );
    } else if (userData.role === "0") {
      return (
        <>
          <Button
            startIcon={<FaceSharpIcon style={{ fill: "white" }} />}
            className={classes.button}
          >
            <Link
              style={{ textDecoration: "none", color: "white" }}
              to="/gestioneleves"
            >
              Gestion des éleves
            </Link>
          </Button>
          <Button
            startIcon={<MenuBookRoundedIcon style={{ fill: "white" }} />}
            className={classes.button}
          >
            <Link
              style={{ textDecoration: "none", color: "white" }}
              to="/gestioncours"
            >
              Gestion des cours
            </Link>
          </Button>
          <Button
            onClick={logout}
            startIcon={<ExitToAppRoundedIcon style={{ fill: "white" }} />}
            className={classes.button}
          >
            <Link
              style={{ textDecoration: "none", color: "white" }}
              to="/signin"
            >
              DECONNEXION
            </Link>
          </Button>
        </>
      );
    } else if (userData.role === "1") {
      return (
        <>
          {}
          <Button
            startIcon={<MenuBookRoundedIcon style={{ fill: "white" }} />}
            className={classes.button}
          >
            <Link
              style={{ textDecoration: "none", color: "white" }}
              to="/courses"
            >
              Mes cours
            </Link>
          </Button>

          <Button
            onClick={logout}
            startIcon={<ExitToAppRoundedIcon style={{ fill: "white" }} />}
            className={classes.button}
          >
            <Link
              style={{ textDecoration: "none", color: "white" }}
              to="/signin"
            >
              DECONNEXION
            </Link>
          </Button>
        </>
      );
    }
  };
  return (
    <div className={classes.root}>
      <Grid container direction="column">
        <Grid item>
          <AppBar position="static">
            <Toolbar>
              <Typography variant="h6" className={classes.title}></Typography>
              <div style={{ marginRight: 220 }}>{onChargeNavBar()}</div>
            </Toolbar>
          </AppBar>
        </Grid>
      </Grid>
    </div>
  );
}
